import React, { useState } from 'react';
import styles from './Gallery.module.css';
import useScrollAnimation from '../../hooks/useScrollAnimation';

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [headerRef, headerVisible] = useScrollAnimation({ threshold: 0.2 });

  const galleryImages = [
    { id: 1, title: 'Hydropower Project Site', category: 'Projects', image: 'https://images.unsplash.com/photo-1473341304170-971dccb5ac1e?auto=format&fit=crop&w=800&q=80', description: 'Construction site of a major hydropower installation' },
    { id: 2, title: 'Renewable Energy Farm', category: 'Projects', image: 'https://images.unsplash.com/photo-1509391366360-2e938d440220?auto=format&fit=crop&w=800&q=80', description: 'Large-scale renewable energy facility' },
    { id: 3, title: 'Solar Installation', category: 'Energy', image: 'https://images.unsplash.com/photo-1508614589041-895b88991e3e?auto=format&fit=crop&w=800&q=80', description: 'Modern solar panel installation' },
    { id: 4, title: 'Dam Structure', category: 'Infrastructure', image: 'https://images.unsplash.com/photo-1581092918056-0c4c3acd3789?auto=format&fit=crop&w=800&q=80', description: 'Engineering marvel - dam infrastructure' },
    { id: 5, title: 'Wind Turbines', category: 'Energy', image: 'https://images.unsplash.com/photo-1532996122724-8f3c2cd83c5d?auto=format&fit=crop&w=800&q=80', description: 'Sustainable wind energy generation' },
    { id: 6, title: 'River Valley', category: 'Projects', image: 'https://images.unsplash.com/photo-1505142468610-359e7d316be0?auto=format&fit=crop&w=800&q=80', description: 'Natural landscape for hydropower development' },
    { id: 7, title: 'Team Meeting', category: 'Team', image: 'https://images.unsplash.com/photo-1552664730-d307ca884978?auto=format&fit=crop&w=800&q=80', description: 'CEC team collaboration and planning' },
    { id: 8, title: 'Project Inspection', category: 'Projects', image: 'https://images.unsplash.com/photo-1581092918056-0c4c3acd3789?auto=format&fit=crop&w=800&q=80', description: 'On-site inspection and monitoring' },
  ];

  const categories = ['All', 'Projects', 'Energy', 'Infrastructure', 'Team'];
  const [selectedCategory, setSelectedCategory] = useState('All');

  const filteredImages = selectedCategory === 'All' 
    ? galleryImages 
    : galleryImages.filter(img => img.category === selectedCategory);

  return (
    <section id="gallery" className={styles.gallery}>
      <div className={styles.container}>
        <div 
          ref={headerRef}
          className={`${styles.sectionHeader} ${headerVisible ? styles.animate : ''}`}
        >
          <h2>Gallery</h2>
          <div className={styles.line}></div>
          <p>Explore our project portfolio and company milestones</p>
        </div>

        {/* Category Filter */}
        <div className={styles.categoryFilter}>
          {categories.map(category => (
            <button
              key={category}
              className={`${styles.categoryBtn} ${selectedCategory === category ? styles.active : ''}`}
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Gallery Grid */}
        <div className={styles.galleryGrid}>
          {filteredImages.map((image, index) => (
            <div 
              key={image.id} 
              className={styles.galleryItem}
              style={{ animationDelay: `${index * 0.05}s` }}
              onClick={() => setSelectedImage(image)}
            >
              <div className={styles.imageContainer}>
                <img src={image.image} alt={image.title} />
                <div className={styles.imageOverlay}>
                  <div className={styles.overlayContent}>
                    <h3>{image.title}</h3>
                    <span className={styles.category}>{image.category}</span>
                  </div>
                  <button className={styles.expandBtn}>üîç</button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Lightbox Modal */}
        {selectedImage && (
          <div className={styles.lightbox} onClick={() => setSelectedImage(null)}>
            <div className={styles.lightboxContent} onClick={(e) => e.stopPropagation()}>
              <button className={styles.closeBtn} onClick={() => setSelectedImage(null)}>‚úï</button>
              <img src={selectedImage.image} alt={selectedImage.title} />
              <div className={styles.lightboxInfo}>
                <h3>{selectedImage.title}</h3>
                <p className={styles.lightboxCategory}>{selectedImage.category}</p>
                <p className={styles.lightboxDescription}>{selectedImage.description}</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Gallery;
